                <path d="M17 29H13C12.4696 29 11.9609 28.7893 11.5858 28.4142C11.2107 28.0391 11 27.5304 11 27V13C11 12.4696 11.2107 11.9609 11.5858 11.5858C11.9609 11.2107 12.4696 11 13 11H17" stroke="white" stroke-linecap="round" stroke-linejoin="round"/>
                <path d="M24 25L29 20L24 15" stroke="white" stroke-linecap="round" stroke-linejoin="round"/>
                <path d="M29 20H17" stroke="white" stroke-linecap="round" stroke-linejoin="round"/>
                </svg>
            </a>
        </div>
        <script defer src="scripts.js"></script>
    </header>
    <main>
        <div class="menu">
            <div class="title">
                <h1 class="title__title">New Post</h1>
                <h2 class="title__subtitle">Fill out the form bellow and publish your article</h2>
            </div> 
            <input class="publish" type="submit" value="Publish"/>
        </div> 
        <div class="status__error">
                <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                    <path d="M12 22C17.5228 22 22 17.5228 22 12C22 6.47715 17.5228 2 12 2C6.47715 2 2 6.47715 2 12C2 17.5228 6.47715 22 12 22Z" stroke="#E86961" stroke-linecap="round" stroke-linejoin="round"/>
                    <path d="M12 8V12" stroke="#E86961" stroke-linecap="round" stroke-linejoin="round"/>
                    <path d="M12 16H12.01" stroke="#E86961" stroke-width="1.5" stroke-linecap="round" stroke-linejoin="round"/>
                </svg>
                <p class="status__text">Whoops! Some fields need your attention :o</p>
        </div>
        <div class="status__success">
            <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
            <path d="M22 11.08V12C21.9988 14.1564 21.3005 16.2547 20.0093 17.9818C18.7182 19.709 16.9033 20.9725 14.8354 21.5839C12.7674 22.1953 10.5573 22.1219 8.53447 21.3746C6.51168 20.6273 4.78465 19.2461 3.61096 17.4371C2.43727 15.628 1.87979 13.4881 2.02168 11.3363C2.16356 9.18455 2.99721 7.13631 4.39828 5.49706C5.79935 3.85781 7.69279 2.71537 9.79619 2.24013C11.8996 1.7649 14.1003 1.98232 16.07 2.85999" stroke="#67C48C" stroke-linecap="round" stroke-linejoin="round"/>
            <path d="M22 4L12 14.01L9 11.01" stroke="#67C48C" stroke-linecap="round" stroke-linejoin="round"/>
            </svg>
            <p class="status__text">Publish Complete!</p>
        </div>
        <div class="main-block">
            <p class="main-block__text">Main Information</p>
            <div class="main-data">
                <div class="main-input">
                    <p class="main-input__title">Title</p>
                    <input id="main-input__title-input" type="text" placeholder="" maxlength="30">
                    <p class="main-input__title-error">Title is  required</p>

                    <p class="main-input__subtitle" >Short description</p>
                    <input id="main-input__subtitle-input" type="text" placeholder="" maxlength="40">
                    <p class="main-input__subtitle-error">Subtitle is  required</p>

                    <p class="main-input__author">Author Name</p>
                    <input class="main-input__author-input" type="text" placeholder="" maxlength="25">
                    <p class="main-input__name-error">Name is  required</p>

                    <p class="main-input__author-photo">Author Photo</p>
                    <div class="main-input__author-photos">
                        <label for="selfiInput">
                            <div class="selfiInput__input">
                                <img class="author-selfi" src="http://localhost:8001/styles/images/add-photo.jpg">
                                <img class="author-update" src="http://localhost:8001/styles/images/update.jpg" alt="">
                            </div>
                            <input type="file" id="selfiInput">                          
                        </label>                       
                        <p class="author-update__text">Upload New</p>
                        <p class="main-input__author-text">Upload</p>
                        
                        <label for="selfiDelete">
                            <img class="delete-selfi" src="http://localhost:8001/styles/images/Delete.jpg">
                            <input type="submit" id="selfiDelete">
                        </label>
                        <p class="selfiDelete__text">Remove</p>
                    </div>
                    <p class="main-input__author-error">Author Photo is required</p>

                    <p class="main-input__publish-date">Publish Date</p>
                    <input id="main-input__publish-input" type="date" value="2023-04-19" />
                    <p class="main-input__date-error">Date is  required</p>
                    
                    <p class="main-input__big-background">Hero Image</p>
                    <label for="BigBackInput">
                        <img class="big-background" src="http://localhost:8001/styles/images/add-big.jpg">
                        <input type="file" accept="image/jpg, image/png, image/jpeg, image/gif" id="BigBackInput">
                    </label>
                    <p class="main-input__big-warn">Size up to 10mb. Format: png, jpeg, gif.</p>
                    <p class="main-input__bigBack-error">Big Background is required</p>

                    <p class="main-input__little-background">Hero Image</p>
                    <label for="LittleBackInput">
                        <img class="little-background" src="http://localhost:8001/styles/images/add-little.jpg">
                        <input type="file" accept="image/jpg, image/png, image/jpeg, image/gif" id="LittleBackInput" >
                    </label>
                    <p class="main-input__little-warn">Size up to 5mb. Format: png, jpeg, gif.</p>
                    <p class="main-input__littleBack-error">Little Background is  required</p>

                    <select name="type" class="main-input__type">
                        <option value="">Please choose type of Post</option>
                        <option value="featured">Featured</option>
                        <option value="recent">Recent</option>
                    </select>
                    <p class="main-input__type-error">Post's type is required</p>

                </div>
                <div class="main-example">
                    <p class="main-example__big-text">Article preview</p>
                    <div class="main-example__big">
                        <div class="main-example__big-inside">
                            <svg class="main-example__big-svg" width="408" height="23" viewBox="0 0 408 23" fill="none" xmlns="http://www.w3.org/2000/svg">
                            <rect x="0.5" y="0.5" width="407" height="22" stroke="#EAEAEA"/>
                            <circle cx="11" cy="12" r="2" fill="#D9D9D9"/>
                            <circle cx="18" cy="12" r="2" fill="#D9D9D9"/>
                            <circle cx="25" cy="12" r="2" fill="#D9D9D9"/>
                            </svg>
                            <div class="main-example__big-content">
                                <p class="main-examplle__big-title">New Post</p>
                                <p class="main-examplle__big-subtitle">Please, enter any description</p>
                            </div>
                            <div class="main-example__big-back">
                                <img class="big-back" src="http://localhost:8001/styles/images/big-back.jpg" alt="Preview Image">
                            </div>
                        </div>
                    </div>

                    <p class="main-example__little-text">Post card preview</p>
                    <div class="main-example__little">
                        <div class="main-example__little-inside">
                            <div class="main-example__little-back">
                                <img class="little-back" src="http://localhost:8001/styles/images/little-back.jpg" alt="Preview Image">
                            </div>
                            <div class="main-example__little-line"></div>
                            <div class="main-example__little-header">
                                <p class="main-example__little-title">Hello</p>
                                <p class="main-example__little-subtitle">Please, enter any description</p>
                            </div>
                            <div class="main-example__little-line"></div>
                                <div class="main-example__little-author">
                                    <div class="main-example__little-selfi">
                                        <img class="little-selfi" src="http://localhost:8001/styles/images/image__background.jpg" alt="Preview Image">
                                    </div>
                                    <p class="main-example__little-name">Enter author name</p>
                                    <p id="main-example__little-date">2023-04-19</p>
                                </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
        <div class="main-text">
            <p class="main-text__text">Content</p>
            <p class="main-text__hint">Post content (plain text)</p>
            <textarea id="main-text__content" rows="7"></textarea>
            <p class="main-input__content-error">Content is  required</p>
        </div>
    </main>
  </body>
</html>